import axios from 'axios';
import React from "react";
import StripeCheckout from 'react-stripe-checkout';
import './Checkout.css';
import backgroundImage from "./cool-background.png";


export class Checkout extends React.Component {
  constructor() {
    super();
    this.componentDidMount = this.componentDidMount.bind(this);
}
state = {
  game: null
};

onToken = (token) => {
  // Send the token to your server to process the payment
  fetch('/api/payment', {
    method: 'POST',
    body: JSON.stringify(token),
  }).then(response => {
    // Show a success message to the user
    console.log('Payment successful!');
  }).catch(error => {
    // Show an error message to the user
    console.error('Payment failed:', error);
  });
}
componentDidMount() {
  axios.get('http://localhost:4000/api/selected-game')
    .then(response => {
      this.setState({ game: response.data });
    })
    .catch(error => {
      console.error(error);
    });
}
  

render() {
  const { game } = this.state;
  if (!game) {
    return <div>There is nothing in your basket, go add some games
    </div>;
    
  }
  return (
    <div style={{ background: `url(${backgroundImage}) no-repeat center center`, backgroundSize: "cover", height: "100vh" }}>
      <div>
      <h1>Checkout</h1>
      <div>
        <h2>Selected Game:</h2>
        <p>Title: {game.title}</p>
        <p>Description: {game.description}</p>
        <p>Price: ${game.price}</p>
      </div>
      
      <StripeCheckout
  name="Game Co."
  description={game.title}
  amount={game.price * 100}
  token={this.onToken}
  stripeKey={process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY}
  billingAddress
  shippingAddress
/>
    </div>
    </div>
  );
}


  
}
