import React from "react";
import Card from 'react-bootstrap/Card';
import Button  from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import axios from "axios";

export class GameItem extends React.Component {
  constructor(){
    super();
    this.DeleteGame = this.DeleteGame.bind(this);
  }
  DeleteGame(e){
    e.preventDefault();

    axios.delete('http://localhost:4000/api/game/'+this.props.game._id)  // update the URL and id to delete a game
    .then((res)=>{this.props.Reload();})
    .catch();
  }
  render() {
    return (
      <div>
        <Card>
          <Card.Header>{this.props.game.name}</Card.Header>  // display the game name in the header
          <Card.Body>
            <blockquote className="blockquote mb-0">
              <img src={this.props.game.image}></img>  // display the game image
              <footer >
                {this.props.game.description}  // display the game description
              </footer>
            </blockquote>
          </Card.Body>


          <Link to={'/edit/' + this.props.game._id} className="btn btn-primary">Edit</Link>  
          <Button variant="danger" onClick={this.DeleteGame}>Delete</Button>
        </Card>
      </div>
    );
  }
}
