import React from "react";
import axios from "axios";

export class Create extends React.Component {

    constructor() {
        super();
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onChangeGameName = this.onChangeGameName.bind(this);
        this.onChangeGameDescription = this.onChangeGameDescription.bind(this);
        this.onChangeStartingPrice = this.onChangeStartingPrice.bind(this);
        this.onChangeGameImage = this.onChangeGameImage.bind(this);  // add this line

        this.state = {
            name: '',
            description: '',
            startingPrice: '',
            image: null  // add this line
        }
    }

    handleSubmit(e){
        const formData = new FormData();
formData.append('name', this.state.name);
formData.append('description', this.state.description);
formData.append('startingPrice', this.state.startingPrice);
formData.append('image', this.state.image);

fetch('http://localhost:4000/api/games', {
  method: 'POST',
  headers: {
    'Content-Type': 'multipart/form-data',
  },
  body: formData,
})
.then(response => response.json())
.then(data => {
  console.log(data);
})
.catch(error => {
  console.log(error);
});

    }


    // add this method
    onChangeGameImage(e) {
        this.setState({
            image: e.target.files[0]
        })
    }

    onChangeGameName(e) {
        this.setState({
            name: e.target.value
        })
    }
    onChangeGameDescription(e) {
        this.setState({
            description: e.target.value
        })
    }
    onChangeStartingPrice(e) {
        this.setState({
            startingPrice: e.target.value
        })
    }

    render() {
        return (
            <div>
                <h3>Hello from Create Component!</h3>
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label>Add Game Name: </label>
                        <input type="text"
                            className="form-control"
                            value={this.state.name}
                            onChange={this.onChangeGameName}
                        />
                    </div>

                    <div className="form-group">
                        <label>Add Game Description: </label>
                        <input type="text"
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChangeGameDescription}
                        />
                    </div>

                    <div className="form-group">
                        <label>Add Starting Price: </label>
                        <input type="text"
                            className="form-control"
                            value={this.state.startingPrice}
                            onChange={this.onChangeStartingPrice}
                        />
                    </div>
                    <div className="form-group">
                        <label>Add Game Image: </label>
                        <input type="file"
                            className="form-control"
                            onChange={this.onChangeGameImage}
                        />
                    </div>

                    <input type="submit" value="Add Game" />
                </form>
            </div>
        );
    }
}
