import React from "react";
import Card from 'react-bootstrap/Card';
import Button  from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import axios from "axios";

export class GameItem extends React.Component {
  constructor(){
    super();
    this.DeleteGame = this.DeleteGame.bind(this);
    this.handleBid = this.handleBid.bind(this);
    this.handleBuyNow = this.handleBuyNow.bind(this);
  }
  DeleteGame(e){
    e.preventDefault();

    axios.delete('http://localhost:4000/api/game/'+this.props.game._id)  // update the URL and id to delete a game
    .then((res)=>{this.props.Reload();})
    .catch();
  }
  handleBid(e) {
    e.preventDefault();
    this.props.handleBid(e, this.props.game._id, this.props.game.price);
  }
  handleBuyNow(e) {
    e.preventDefault();
    this.props.handleBuyNow(e, this.props.game._id, this.props.game.price);
  }
  render() {
    return (
      <div>
        <Card>
          <Card.Header>{this.props.game.name}</Card.Header>  
          <Card.Body>
            <blockquote className="blockquote mb-0">
              <img src={this.props.game.image}></img> 
              <footer >
                {this.props.game.description}  
              </footer>
            </blockquote>
          </Card.Body>
          <div>
            <label>Current price: {this.props.game.price}</label>
            <br />
            <label>Enter bid amount:</label>
            <input type="number" min={this.props.game.price + 1} />
            <button onClick={this.handleBid}>Place bid</button>
            <button onClick={this.handleBuyNow}>Buy now</button>
          </div>
          <Link to={'/edit/' + this.props.game._id} className="btn btn-primary">Edit</Link>  
          <Button variant="danger" onClick={this.DeleteGame}>Delete</Button>
         </Card>       
         </div>
     );
   }
 }
