import React from "react";
import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import axios from "axios";
import backgroundImage from "./cool-background.png";

export class GameItem extends React.Component {
  constructor() {
    super();
    this.DeleteGame = this.DeleteGame.bind(this);
    this.handleBid = this.handleBid.bind(this);
    this.handleBuyNow = this.handleBuyNow.bind(this);
  }
  DeleteGame(e) {
    e.preventDefault();

    axios.delete('http://localhost:4000/api/game/' + this.props.game._id)  // update the URL and id to delete a game
      .then((res) => { this.props.Reload(); })
      .catch();
  }
  handleBid(e) {
    e.preventDefault();
    const bidAmount = e.target.parentNode.querySelector('input').value;
    axios.post(`http://localhost:4000/api/game/${this.props.game._id}/bid`, { bidAmount })
      .then((res) => {
        this.props.Reload();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  handleBuyNow(e) {
    e.preventDefault();
    axios.post(`http://localhost:4000/api/game/${this.props.game._id}/buy`)
      .then((res) => {
        this.props.Reload();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <div style={{ background: `url(${backgroundImage}) no-repeat center center`, backgroundSize: "cover", height: "100vh" }}>
        <Card>
          <Card.Header>{this.props.game.name}</Card.Header>
          <Card.Body>
            <blockquote className="blockquote mb-0">
              <img src={this.props.game.image} style={{ background: `url(${backgroundImage}) no-repeat center center`, backgroundSize: "cover", height: "100vh" }}></img >
              <footer >
                {this.props.game.description}
              </footer>
            </blockquote>
          </Card.Body>
          <div style={{ background: `url(${backgroundImage}) no-repeat center center`, backgroundSize: "cover", height: "100vh" }}>
          <label>Current price: {this.props.price}</label>
            <label>Current price: {this.props.game.startingPrice}</label>  // display the price prop here
            <br />
            <label>Enter bid amount:</label>
            <input type="number" min={this.props.game.startingPrice + 1} />
            <button onClick={(e) => this.props.handleBid(e, this.props.game._id, this.props.game.price)}>Place bid</button>
            <button onClick={this.handleBuyNow}>Buy now</button>
          </div>
          <Link to={'/edit/' + this.props.game._id} className="btn btn-primary">Edit</Link>
          <Button variant="danger" onClick={this.DeleteGame}>Delete</Button>
        </Card>       
        </div>
    );
  }

}
