import React from "react";
import { Games } from "./games";  
import axios from "axios";

export class Read extends React.Component {
  constructor() {
    super();
    this.componentDidMount = this.componentDidMount.bind(this);
  }

  componentDidMount() {
    axios.get('http://localhost:4000/api/games')  // update the URL to retrieve games
        .then((response) => {
            this.setState({ games: response.data })  // update the state key to store games
        })
        .catch((error) => {
            console.log(error);
        })
  }

  state = {
    games: []  // update the initial state to an empty array of games
  }

  render() {
    return (
      <div>
        <h3>Welcome to the auction house here is out selection of games you can buy</h3>
                {this.state.games.map((game) => (
          <div>
            <h4>{game.name}</h4>
            <p>Current price: {game.price}</p>
          </div>
        ))}
        <Games games={this.state.games} Reload={this.componentDidMount} handleBid={this.handleBid} handleBuyNow={this.handleBuyNow}></Games>  
      </div>
    );
  }
}
