import React from "react";
import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import axios from "axios";
import backgroundImage from "./cool-background.png";
import "./GameItem.css";

export class GameItem extends React.Component {
  constructor() {
    super();
    this.DeleteGame = this.DeleteGame.bind(this);
    this.handleBid = this.handleBid.bind(this);
    this.handleBuyNow = this.handleBuyNow.bind(this);
  }
  DeleteGame(e) {
    e.preventDefault();

    axios.delete('http://localhost:4000/api/game/' + this.props.game._id)  // update the URL and id to delete a game
      .then((res) => { this.props.Reload(); })
      .catch();
  }
  handleBid(e) {
    e.preventDefault();
    const bidAmount = e.target.parentNode.querySelector('input').value;
    axios.post(`http://localhost:4000/api/game/${this.props.game._id}/bid`, { bidAmount })
      .then((res) => {
        this.props.Reload();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  handleBuyNow(e) {
    e.preventDefault();
    axios.post(`http://localhost:4000/api/game/${this.props.game._id}/buy`)
      .then((res) => {
        this.props.Reload();
      })
      .catch((error) => {
        console.log(error);
      });
  }
  render() {
    return (
      <div className="card">
        <Card className="cards">
          <Card.Body className="card-body">
          <label className="card-header">{this.props.game.name}</label>

              <img className="image" src={this.props.game.image}></img>
              <footer className="card-footer">
                {this.props.game.description}
              </footer>


          <div>
            <label>Current price: {this.props.game.startingPrice}</label>
            <br />
            <label>Enter bid amount:</label>
            <input type="number" min={this.props.game.price + 1} />
            <div className="buttons">
            <button className="add-to-cart-button" onClick={(e) => this.props.handleBid(e, this.props.game._id, this.props.game.price)}>Place bid</button>
              <button className="add-to-cart-button" onClick={this.handleBuyNow}>Buy now</button>
            </div>
          </div>

          <Button className="button" variant="danger" onClick={this.DeleteGame}>Delete</Button>
          </Card.Body>
        </Card>       
      </div>
    )};



}
