import React from "react";
import Card from 'react-bootstrap/Card';
import Button from "react-bootstrap/Button";
import axios from "axios";
import "./GameItem.css";
import StripeCheckout from 'react-stripe-checkout';

export class GameItem extends React.Component {
  constructor() {
    super();
    this.DeleteGame = this.DeleteGame.bind(this);
    this.handleBidSubmit = this.handleBidSubmit.bind(this);
  }

  DeleteGame(e) {
    e.preventDefault();

    axios.delete('http://localhost:4000/api/game/' + this.props.game._id)  // update the URL and id to delete a game
      .then((res) => { this.props.Reload(); })
      .catch();
  }

  handleBidSubmit(e) {
    e.preventDefault();

    // send a PUT request to the server to update the game with the new bid amount
    axios.put('http://localhost:4000/api/game/' + this.props.game._id, {
      price: document.getElementById('bidAmount').value
    })
      .then((res) => { this.props.Reload(); })
      .catch();
  }

  render() {
    return (
      <div className="card">
        <Card className="cards">
          <Card.Body className="card-body">
            <label className="card-header">{this.props.game.name}</label>

            <img className="image" src={this.props.game.image}></img>
            <footer className="card-footer">
              {this.props.game.description}
            </footer>

            <form onSubmit={this.handleBidSubmit}>
              <label>Current price: {this.props.game.price || this.props.game.startingPrice}</label>
              <br />
              <label>Enter bid amount:</label>
              <input type="number" id="bidAmount" min={isFinite(this.props.game.price) ? this.props.game.price + 1 : ''} />
              <br />
              <Button type="submit" variant="primary">Place bid</Button>
              <StripeCheckout //Stripe checkout implemented - it requires a key in order to autheticate the purchase, i didnt bother to get a key as its there just for show really
                          //Once you click pay with card an error will popup but then the checkout window will aslo pop up allowing the user to checkout
            name="Games4U Co."
            description={this.game.title}
            amount={this.props.game.startingPrice * 100} 
            token={this.onToken}
            stripeKey={process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY}
            billingAddress
            shippingAddress
          />
            </form>

            <div className="buttons">
            </div>

            <Button className="button" variant="danger" onClick={this.DeleteGame}>Delete</Button>
          </Card.Body>
        </Card>
      </div>
    )
  };
}
