import React from "react";
import { Games } from "./games";  // import the Games presentation component
import axios from "axios";

export class Read extends React.Component {
    constructor() {
        super();
        this.componentDidMount = this.componentDidMount.bind(this);
    }

    componentDidMount() {
        axios.get('http://localhost:4000/api/games')  // update the URL to retrieve games
            .then((response) => {
                this.setState({ games: response.data })  // update the state key to store games
            })
            .catch((error) => {
                console.log(error);
            })
    }

    state = {
        games: []  // update the initial state to an empty array of games
    }

    render() {
        return (
            <div>
                <h3>Hello from my Read component!</h3>
                <Games games={this.state.games} Reload={this.componentDidMount}></Games>  // pass the games and Reload prop to the Games presentation component
            </div>
        );
        }
    }
